import React, { useState, useEffect, useRef } from 'react'
import axios from 'axios'
import { io } from 'socket.io-client'
import { useNavigate } from 'react-router-dom';
const Home = () => {
  const socket = useRef();
  const navigate = useNavigate();
  const [currentUser, setCurrentUser] = useState(undefined);
  const [userList, setUserList] = useState([]);
  const [onlineUser, setOnline] = useState([]);
  const [onlineUserList, setOnlineUserList] = useState([]);


  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem("user")) {
      navigate("/")
    } else {
      setCurrentUser(JSON.parse(localStorage.getItem("user")))
      setIsLoaded(true)

    }
  }, [])
  useEffect(() => {
    if (currentUser) {
      console.log(currentUser)
      socket.current = io("http://localhost:5000");
      socket.current.emit("add-user", currentUser.id);
      socket.current.on("get-users", async (users) => {
        const data = await axios.get("http://localhost:5000/getallusers");
        setUserList(data.data)
        setOnline(users)
        console.log(data.data)
        console.log(users)

      })
    }
  }, [currentUser])
  useEffect(() => {

    var obj = {}
    for (var i = 0; i < userList.length; i++) {
      var id = userList[i].id
      if (!obj[id]) {
        obj[id] = { username: userList[i].username, connect: false, email: userList[i].email, id: userList[i].id }
      }
      if (onlineUser.length > 0) {
        for (var j = 0; j < onlineUser.length; j++) {
          if (userList[i].id == onlineUser[j].userId) {
            obj[id].connect = true
          }
        }
      }
    }

    setOnlineUserList(obj)
    console.log(onlineUserList)
    console.log(obj)
  }, [onlineUser])
  return (
    isLoaded &&
    <div className='container'>
      <div className='curent_user'>
        {currentUser.username}
      </div>
      <div className="contacts">
        {
          Object.keys(onlineUserList).map((key, index) => {
            return (
              <div className="contect" key={index}>
                <div className="avatar">

                </div>
                <div className="username">
                  <h3>{onlineUserList[key].username}</h3>
                </div>
                <div className="username">
                  <h3>{onlineUserList[key].connect == true ? "Online" : "Offline"}</h3>
                </div>

              </div>
            )
          })
        }
      </div>

    </div>
  )
}

export default Home
