import React, { useEffect ,useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";
import "./login.css";

const Login = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    username: "",
    password: "",
  })
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (handleValidation()) {
      const { password, username, } = values;
      const { data } = await axios.post("login", {
        username, password
      })
      if (data.status == false) {
        alert(data.msg)
      }
      if (data.status == true) {
        navigate("/home");
      }
    }
  }
  const handleValidation = () => {
    const { password, username } = values;
    if (password === "") {
      alert("Password is not null")
      return false;
    } else if (username === "") {
      alert("Username is not null")
      return false;
    }
    return true;
  }
  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value })

  }
  return (
    <div className='login__container'>

      <form onSubmit={(event) => handleSubmit(event)} className="login__form">
        <div className="title">
          <h3>Chat App</h3>
        </div>
        <input type="text" name="username" id="" placeholder='username' onChange={(e) => handleChange(e)} />
        <input type="password" name="password" id="" placeholder='password' onChange={(e) => handleChange(e)} />
        <button type='submit'>Log In</button>
        <span>
          Don't have an account ? <Link to="/register">Create One.</Link>
        </span>

      </form>

    </div>
  )
}

export default Login
